import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { Button } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

function Dashboard() {

  const [FunctionOutput, setFunctionOutput] = useState(0);
  const [PrintOutputs, setPrintOutput] = useState(0);
  const [name, setName] = useState('');
  const editor = document.querySelector(".editor");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`The name you entered was: ${name}`)
  }

  useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setFunctionOutput(data.time);
      setPrintOutput(data.prints);
    });
  }, []);


  function buttonpress(){
    // Simple POST request with a JSON body using fetch
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title: editor.textContent })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`The name you entered was: ${name}`);
  }

  fetch('/write', requestOptions)
      .then(response => response.json());
  };

  return (
    <div className="Dashboard">

      <header className="App-header">
      <div class="btn-toolbar" role="toolbar" aria-label="Toolbar with button groups"> 
        <div class="btn-group mr-2" role="group" aria-label="First group"> <button class="btn btn-outline-light" type="button">Light Mode</button> </div>
        <div class="btn-group mr-2" role="group" aria-label="Second group"> <button class="btn btn-sm btn-outline-dark" type="button">Dark Mode</button> </div>
        <div class="btn-group mr-2" role="group" aria-label="Third group"> <button class="btn btn-sm btn-outline-info" type="button">Info</button> </div>
        <div class="btn-group mr-2" role="group" aria-label="Second group"> <button class="btn btn-sm btn-outline-success" type="button">Run</button> </div>
      </div>
      <div class="editor-menu">
        <Button variant="light">Light Mode</Button>
        <Button variant="dark" className="mx-2">Dark Mode</Button>
        <Button variant="info">Info</Button>
        <Button variant="success" onClick={buttonpress}>Run Code</Button>
    </div>

    <div class="mycontainer">
        <div class="myleft">
            <div class="myeditor" contenteditable="">
                Write Code here...
            </div>
        </div>
        <div class="mybar"title="Click and Drag"></div>
        <div class="myright">
        <div class="mymids">
          <p>{FunctionOutput}.</p>
        </div>
        <div class="mymids">
        <p>{PrintOutputs}</p>
        </div>
        </div>
    </div>
      </header>
    </div>
  );
}

export default Dashboard;
